import React from 'react';
import { View, Text } from 'react-native';
import { Entypo, AntDesign, Fontisto } from 'react-native-vector-icons';

import styles from './styles';
import Icon from '../../Icon';
import { useNavigation, useRoute } from '@react-navigation/native';
import { COLORS } from '../../../utils';

const Nav = () => {
  const { navigate } = useNavigation();
  const { name: routeName } = useRoute();

  const [isKeyboardVisible, setKeyboardVisible] = useState(false);

  useEffect(() => {
    const keyboardDidShowListener = Keyboard.addListener(
      'keyboardDidShow',
      () => {
        setKeyboardVisible(true); // or some other action
      }
    );
    const keyboardDidHideListener = Keyboard.addListener(
      'keyboardDidHide',
      () => {
        setKeyboardVisible(false); // or some other action
      }
    );

    return () => {
      keyboardDidHideListener.remove();
      keyboardDidShowListener.remove();
    };
  }, []);

  const active = {
    color: COLORS.green,
    borderColor: COLORS.green,
    borderWidth: 1,
  };

  return (
    <>
      <View style={{ height: '8%' }}></View>
      <View style={styles.container}>
        <View style={styles.icons}>
          <Icon
            element={AntDesign}
            name="home"
            onPress={() => navigate('Home')}
            style={['Home', 'Product Details'].includes(routeName) && active}
          />
          <Icon element={Fontisto} name="shopping-sale" type="primary" />
          <Icon
            element={AntDesign}
            name="shoppingcart"
            onPress={() => navigate('Cart')}
            style={['Cart', 'Payment'].includes(routeName) && active}
          />
          <Icon
            element={AntDesign}
            name="login"
            type="primary"
            onPress={() => navigate('Sign In')}
            style={['Sign In', 'Sign Up'].includes(routeName) && active}
          />
          {/* <Icon element={MaterialCommunityIcons} name="account" type="primary" /> */}
          {/* <Icon element={AntDesign} name="logout" type="primary" /> */}
        </View>
      </View>
    </>
  );
};

export default Nav;
