import { StyleSheet } from 'react-native';

import { FONTS } from '../../../utils';

const styles = StyleSheet.create({
  container: {
    padding: 16,
  },
  contentContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
  },
  itemContainer: {
    justifyContent: 'center',
    alignItems: 'center',
    width: '33%',
  },
  image: {
    resizeMode: 'contain',
    width: 90,
    height: 110,
  },
  titleText: {
    fontSize: 16,
    padding: 16,
    paddingBottom: 0,
    color: COLORS.black,
    fontWeight: 'bold',
    letterSpacing: 1,
  },
});

export default styles;
