import React, { useState } from 'react';
import { View, Text, TouchableOpacity } from 'react-native';

import styles from './styles';
import { AuthLayout, FormInput } from '../../../components';
import { ICONS } from '../../../utils';

const SignIn = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailError, setEmailError] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  return (
    <AuthLayout title="Sign In">
      <FormInput
        label="Email"
        keyboardType="email-address"
        autoCompleteType="email"
        onChange={value => {
          // validate email .
          setEmail(value);
        }}
        errorMsg={emailError}
        appendComponent={
          <View style={styles.emailAppendContainer}>
            <Image style={styles.emailAppendImage} source={ICONS.correct} />
          </View>
        }
      />

      <FormInput
        label="Password"
        secureTextEntry={!showPass}
        autoCompleteType="password"
        containerStyle={styles.passwordContainer}
        onChange={value => setPassword(value)}
        appendComponent={
          <TouchableOpacity
            style={styles.passwordAppendContainer}
            onPress={() => setShowPass(!showPass)}
          >
            <Image
              style={styles.passwordAppendImage}
              source={showPassword ? ICONS.eye : ICONS.eye}
            />
          </TouchableOpacity>
        }
      />
    </AuthLayout>
  );
};

export default SignIn;
