import React, { useState } from 'react';
import { View, Text, TouchableOpacity, Image } from 'react-native';
import { Entypo } from 'react-native-vector-icons';

import styles from './styles';
import { AuthLayout, FormInput } from '../../../components';
import { COLORS2 as COLORS, ICONS, validateEmail } from '../../../utils';

const SignIn = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailError, setEmailError] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  return (
    <AuthLayout title="Sign In">
      <FormInput
        label="Email"
        keyboardType="email-address"
        autoCompleteType="email"
        onChange={value => {
          validateEmail(value, setEmailError);

          setEmail(value);
        }}
        errorMsg={emailError}
        appendComponent={
          <View style={styles.emailAppendContainer}>
            <Image
              source={
                email === '' || (email !== '' && emailError === '')
                  ? ICONS.correct
                  : ICONS.cancel
              }
            />
            <Entypo
              name={showPassword ? 'eye-with-line' : 'eye'}
              style={{
                height: 20,
                width: 20,
                tintColor:
                  email === ''
                    ? COLORS.grey
                    : email !== '' && emailError === ''
                    ? COLORS.green
                    : COLORS.red,
              }}
            />
          </View>
        }
      />

      <FormInput
        label="Password"
        secureTextEntry={!showPassword}
        autoCompleteType="password"
        containerStyle={styles.passwordContainer}
        onChange={value => setPassword(value)}
        appendComponent={
          <TouchableOpacity
            style={styles.passwordAppendContainer}
            onPress={() => setShowPassword(!showPassword)}
          >
            <Entypo
              name={showPassword ? 'eye-with-line' : 'eye'}
              style={styles.passwordAppendImage}
            />
          </TouchableOpacity>
        }
      />
    </AuthLayout>
  );
};

export default SignIn;
